const ZK = require('../index')
const path = '/DP/CONFIG/static-server.liontest';

(async () => {
    let client = new ZK()

    await client.init()

    let res = await client.getAndWatch(path, value => {
        console.log(value)
    })
    console.log(res)

    let exists = await client.existsAndWatch(path, value => {
        console.log(value)
    })
    console.log(exists)
})()
